    {
        protected abstract void Startup();
        protected abstract void Run();
        protected abstract void Turn();
        protected abstract void Stop();

        public void Test()
        {
            Startup();//晚绑定，留给应用程序开发人员实现，扩展点
            //测试数据记录...

            Run();//晚绑定，留给应用程序开发人员实现，扩展点
            //测试数据记录...

            Turn();//晚绑定，留给应用程序开发人员实现，扩展点
