     }
     public override void  Draw()
     {
       //PC上的实现代码
     }

     public override void  Stop()
     {
        //PC上的实现代码
     }
 }
/// <summary>
/// Mobile平台的Tank
/// </summary>
public class MobileTankImplementation:TankPlatformImplementation
{

     public override void  MoveTo()
     {
       // Mobile上的实现代码
     }

     public override void  Draw()
     {
       // Mobile上的实现代码
     }

     public override void  Stop()
     {
        // Mobile上的实现代码
     }
}
```

不同型号坦克的子类实现

```C#
