{
    public class DaZhongCar:Vehicle
    {
        protected override void Startup()
        {
            //...
        }

        protected override void Run()
        {
            //...
        }

        protected override void Turn()
        {
            //...
        }

        protected override void Stop()
        {
            //...
        }
    }
}
```

